  ) => {
    // ...
    createDiscount.mutate({
      code: currencyCode,
      rule: {
        type: DiscountRuleType.FIXED,
        value: 10,
        allocation: AllocationType.ITEM,
      },
      regions: [
          regionId,
      ],
      is_dynamic: false,
      is_disabled: false,
    })
  }

  // ...
}

export default CreateDiscount
